.globl  vbsme


# Begin subroutine
vbsme:  

    addi $v0, $zero, 0
    addi $v1, $zero, 0
    addi $s0, $zero, 1
    sll $s0, $s0, 30

    addi $t2, $zero, 60
    addi $t3, $zero, 4
    addi $t4, $zero, 64


    addi $s2, $zero, 0
    FRAME_X:
    	addi $s3, $zero, 0
        FRAME_Y:

    	    addi $s1, $zero, 0

    	    addi $s7, $zero, 0
            WINDOW_Y:

    	    	addi $s6, $zero, 0
                WINDOW_X:

                    mul $t5, $t4, $s7
                    add $t5, $t5, $s6
                    mul $t6, $t4, $s3
                    add $t5, $t5, $t6
                    add $s4, $t5, $s2

                    mul $t5, $t3, $s7
                    add $s5, $s6, $t5

                    sll $s4, $s4, 2
                    add $t8, $a1, $s4
                    lw $t0, 0($t8)
                    sll $s5, $s5, 2
                    add $t8, $a2, $s5
                    lw $t1, 0($t8)

                    sub $t9, $t0, $t1

                    bgtz $t9, POS
                        sub $s1, $s1, $t9
                        j END
                        POS: 
			   add $s1, $s1, $t9
                        END:

                    addi $s6, $s6, 1
                bne $s6, $t3, WINDOW_X
                addi $s7, $s7, 1
            bne $s7, $t3, WINDOW_Y

            sub $t9, $s1, $s0
            bgez $t9, SKIP
                addi $s0, $s1, 0
                addi $v0, $s3, 0
                addi $v1, $s2, 0
            SKIP:
            
            addi $s3, $s3, 1
        bne $s3, $t2, FRAME_Y
        addi $s2, $s2, 1
    bne $s2, $t2, FRAME_X


        jr $ra


   
